import React from "react";
import axios from "axios";
import './searchresults.css'
import Displaybooks from "../Displaybooks/Displaybooks"
import Nav from "../Nav/Nav"
import Header from "../Header/Header"
import API from "../../utils/API"

export default class Searchresults extends React.Component {
    state = {
        names: '',
        title: "",
        author: "",
        description: "",
        infoLink: "",
        imageLink: "",
        _id:"",
        items: []
    };

    loadBooks = () => {
        console.log(" I need to save this book")

    }

    savebook = () => {
        API.saveBook({
            title: this.state.title,
            author: this.state.author,
            description: this.state.description,
            infoLink: this.state.infoLink,
            imageLink: this.state.imageLink,
            id: this.state.id
        })
            .then(res => cons)
            .catch(err => console.log(err));
    }

    handleChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }


    handleSubmit = event => {
        event.preventDefault();

        let query = {
            names: this.state.names,
        };
        // const link = "https://www.googleapis.com/books/v1/volumes?q"
        if (query.names) {
            axios.get(`https://www.googleapis.com/books/v1/volumes?q=${query.names}`)

                .then(res => {
                    this.setState({ items: res.data.items })
                    let names = this.state.items
                    console.log(names)
                    console.log(query)
                    console.log(res)
                }
                )
                .catch(err => console.log(err));

        } else console.log("type an author name")

    }

    render() {
        const { items } = this.state

        return (
            <div  >
                <Nav />
                <Header />
                <form className="p-4 " onSubmit={this.handleSubmit}>

                    <input className="form-control mb-2" type="text" name="names" value={this.state.names} onChange={this.handleChange} placeholder="search for a  book" />

                    <button className="btn btn-info" type="submit"> Search</button>
                </form>
                <span> {items.map((item) => {
                    let { title, description, imageLinks, authors, infoLink } = item.volumeInfo
                    console.log( "The item id are " + item.id)
                    return(
                        <div key={item.id}> 
                            <Displaybooks title={title} description={description} imageLinks={imageLinks} authors={authors} infoLink={infoLink} id={item.id}
                            onClick={() => this.savebook()} 
                        />
                        </div>
                         
                    )

                }
                )}
                </span>


            </div>
        )
    }

}
